import React, { Component } from 'react'
import PostForm from './PostForm'

export default class NewPost extends Component {
  state = {

  }
  handleInput = (e) => {
    const formData = {}
    formData[e.target.name] = e.target.value
    this.setState({ ...formData })
  }
  render() {
    const { title, body } = this.state
    return (
      <div>
        <h1>New Post</h1>
        {/* <PostForm /> */}
        <form>
          <input type="text" onChange={this.handleInput} value={title} placeholder="title" />
          <textarea type="text" onChange={this.handleInput} value={body} placeholder="body" id="" cols="30" rows="10" />
          <button>Submit</button>
        </form>
      </div>
    )
  }
}
